/*global tinymce:true */

tinymce.PluginManager.add('emoticons', function(editor, url) {

	var emoticons = [
        ["blink","evilmad","huh","laugh","cryy"],
        ["laught","lol3","look","ohmy","rolleyes"],
        ["shifty","sorry2","spiteful","unsure","thumbup"],
        ["up","w00t","wacko","wink2","yawn"]
	];



	function getHtml() {
		var emoticonsHtml;

		emoticonsHtml = '<table role="presentation" class="mce-grid">';

		tinymce.each(emoticons, function(row) {
			emoticonsHtml += '<tr>';

			tinymce.each(row, function(icon) {
				var emoticonUrl = url+'/smilies/' + icon + '.gif';

				emoticonsHtml += '<td><a href="#" data-mce-url="' + emoticonUrl + '" tabindex="-1"><img src="' +
					emoticonUrl + '" style="padding:2px;"></a></td>';
			});

			emoticonsHtml += '</tr>';
		});

		emoticonsHtml += '</table>';

		return emoticonsHtml;
	}

	editor.addButton('emoticons', {
		type: 'panelbutton',
		popoverAlign: 'bc-tl',
		panel: {
			autohide: true,
			html: getHtml,
			onclick: function(e) {
				var linkElm = editor.dom.getParent(e.target, 'a');

				if (linkElm) {
					editor.insertContent('<img src="' + linkElm.getAttribute('data-mce-url') + '" />');
					this.hide();
				}
			}
		},
		tooltip: 'Emoticons'
	});
/*
	editor.addButton('emoticons', {
		type: 'panelbutton',
		popoverAlign: 'bc-tl',
		panel: {
			layout: 'flex',
			spacing: 5,
			padding: 5,
			autohide: true,
			items: [
				{type: 'textbox', label: 'Url'},
				{type: 'button', text: 'OK'}
			]
		},
		tooltip: 'Emoticons'
	});*/
});
